import React, { useState } from 'react';
import { walletAPI } from '../services/api';

const WalletActions = ({ user, onUpdate }) => {
  const [showModal, setShowModal] = useState(null);
  const [formData, setFormData] = useState({ amount: '', details: '' });
  const [loading, setLoading] = useState(false);

  const handleDeposit = async (type) => {
    if (type === 'inr') {
      handleRazorpayPayment();
    } else {
      setLoading(true);
      try {
        await walletAPI.requestDeposit({
          type,
          amount: parseFloat(formData.amount),
          paymentMethod: 'USDT Transfer',
          transactionId: formData.details
        });
        alert('USDT deposit request submitted!');
        setShowModal(null);
        setFormData({ amount: '', details: '' });
      } catch (error) {
        alert(error.response?.data?.message || 'Error submitting deposit');
      }
      setLoading(false);
    }
  };

  const handleRazorpayPayment = async () => {
    setLoading(true);
    try {
      const response = await walletAPI.createPaymentOrder(parseFloat(formData.amount));
      const { order, key } = response.data;

      const options = {
        key,
        amount: order.amount,
        currency: order.currency,
        name: 'P2P Trading Platform',
        description: 'INR Deposit',
        order_id: order.id,
        handler: async (response) => {
          try {
            const verifyResponse = await walletAPI.verifyPayment({
              paymentId: response.razorpay_payment_id,
              orderId: response.razorpay_order_id,
              signature: response.razorpay_signature,
              amount: formData.amount
            });

            if (verifyResponse.data.success) {
              onUpdate(verifyResponse.data.wallets);
              alert('INR deposited successfully!');
              setShowModal(null);
              setFormData({ amount: '', details: '' });
            }
          } catch (error) {
            alert('Payment verification failed');
          }
        },
        prefill: {
          name: user.name,
          email: user.email
        }
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      alert('Error creating payment order');
    }
    setLoading(false);
  };

  const handleWithdraw = async (type) => {
    setLoading(true);
    try {
      if (type === 'usdt') {
        const response = await walletAPI.processUSDTWithdrawal({
          amount: parseFloat(formData.amount),
          address: formData.details
        });

        if (response.data.success) {
          onUpdate(response.data.wallets);
          alert(`USDT sent successfully! TX: ${response.data.transaction}`);
        }
      } else {
        await walletAPI.requestWithdrawal({
          type,
          amount: parseFloat(formData.amount),
          withdrawalDetails: formData.details
        });
        alert('INR withdrawal request submitted!');
      }

      setShowModal(null);
      setFormData({ amount: '', details: '' });
    } catch (error) {
      alert(error.response?.data?.message || 'Error processing withdrawal');
    }
    setLoading(false);
  };

  const renderModal = () => {
    if (!showModal) return null;

    const isDeposit = showModal.includes('deposit');
    const type = showModal.replace('deposit-', '').replace('withdraw-', '');

    return (
      <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
        <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '8px', maxWidth: '400px', width: '90%' }}>
          <h3>{isDeposit ? 'Deposit' : 'Withdraw'} {type.toUpperCase()}</h3>

          <input
            type="number"
            placeholder="Amount"
            value={formData.amount}
            onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
            style={{ width: '100%', padding: '10px', margin: '10px 0', border: '1px solid #ddd', borderRadius: '4px' }}
          />

          {!(isDeposit && type === 'inr') && (
            <input
              type="text"
              placeholder={isDeposit ? 
                'Transaction Hash' : 
                (type === 'inr' ? 'Your UPI ID' : 'Your USDT Address')
              }
              value={formData.details}
              onChange={(e) => setFormData({ ...formData, details: e.target.value })}
              style={{ width: '100%', padding: '10px', margin: '10px 0', border: '1px solid #ddd', borderRadius: '4px' }}
            />
          )}

          {isDeposit && (
            <div style={{ backgroundColor: '#f8f9fa', padding: '15px', borderRadius: '4px', margin: '10px 0', fontSize: '14px' }}>
              {type === 'inr' ? (
                <div>
                  <p><strong>Automated Payment via Razorpay</strong></p>
                  <p>Click Submit to open secure payment gateway</p>
                </div>
              ) : (
                <div>
                  <p><strong>Send to:</strong></p>
                  <p style={{ wordBreak: 'break-all' }}>TQn9Y2khEsLMWD5uP5sVxnzeLcEwQQhAvh</p>
                  <p>Enter transaction hash after sending</p>
                </div>
              )}
            </div>
          )}

          {!isDeposit && type === 'usdt' && (
            <div style={{ backgroundColor: '#fff3cd', padding: '15px', borderRadius: '4px', margin: '10px 0', fontSize: '14px' }}>
              <p><strong>Instant Withdrawal</strong></p>
              <p>USDT will be sent immediately to your address</p>
            </div>
          )}

          <div style={{ display: 'flex', gap: '10px', marginTop: '20px' }}>
            <button
              onClick={() => isDeposit ? handleDeposit(type) : handleWithdraw(type)}
              disabled={loading || !formData.amount || !formData.details}
              style={{ flex: 1, padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              {loading ? 'Processing...' : 'Submit'}
            </button>
            <button
              onClick={() => setShowModal(null)}
              style={{ flex: 1, padding: '10px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div style={{ marginBottom: '20px' }}>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
        <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
          <h4>INR Wallet: ₹{user.wallets?.inr?.toFixed(2) || '0.00'}</h4>
          <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
            <button
              onClick={() => setShowModal('deposit-inr')}
              style={{ flex: 1, padding: '8px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Deposit
            </button>
            <button
              onClick={() => setShowModal('withdraw-inr')}
              disabled={!user.wallets?.inr || user.wallets.inr <= 0}
              style={{ flex: 1, padding: '8px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Withdraw
            </button>
          </div>
        </div>

        <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px' }}>
          <h4>USDT Wallet: {user.wallets?.usdt?.toFixed(6) || '0.000000'}</h4>
          <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
            <button
              onClick={() => setShowModal('deposit-usdt')}
              style={{ flex: 1, padding: '8px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Deposit
            </button>
            <button
              onClick={() => setShowModal('withdraw-usdt')}
              disabled={!user.wallets?.usdt || user.wallets.usdt <= 0}
              style={{ flex: 1, padding: '8px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Withdraw
            </button>
          </div>
        </div>
      </div>

      {renderModal()}
    </div>
  );
};

export default WalletActions;
